injectCSS:
body {
  background-color: #a7ff00;
}

.window-content {
  background-color: rgba(0, 0, 0, 0.0); /* Transparent  background for content */
  border: 2px solid #000000;
}

.terminal {
    border: 2px solid #000000;
    border-radius: 8px; /* Rounded corners for the terminal*/
    background-color: rgba(0,0,0,0.0);
  }

.terminal-text-output {
      color: #000000;
    }

.keyboard {
  border: 2px solid #000000;
     background-color: rgba(0,0,0,0.0);

}

.keyboard-key {
      color: #000000;
      font-family: 'Durandal Grotesque';
        font-size: 18px;
  }

.keyboard-key:active {
    background-color: #000000;
     color: #000000;
  }

.filesystem-browser-folder, .filesystem-browser-file {
      color: #000000;
    font-size: 18px;
  }

  .top-processes-list-header-item, .top-processes-list-item, .system-info-item {
  color: #000000;
  font-size: 18px;
  }
  .terminal-tab-label {
       color: #000000;
     font-size: 18px;
    }

    .filesystem-browser-item:hover, .top-processes-list-item:hover, .system-info-item:hover, .terminal-tab-label:hover  {
        background-color: rgba(0,0,0, 0.2);
    }

.window-content::-webkit-scrollbar-thumb {
     background-color: #000000;
}
